import React from 'react';
import ReactDOM from 'react-dom';
import { useState } from 'react';
import './index.css';
import reportWebVitals from './reportWebVitals';
import './App.css';
import SearchBar from './search';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";


const client = new ApolloClient({
  uri: "https://api.github.com/graphql",
  cache: new InMemoryCache(),
  headers: {
    authorization: `Bearer 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`
  }
});

const GET_REPOSITORIES = gql`
   query repositories {
    viewer {
        login
        repositories(first: 30) {
            edges {
                node {
                name
                }
            }
        }
    }
}
`;


const filterRepos = (repos, query) => {
  if (!query) {
    return repos;
  }
  const finalRepos = repos && repos.filter((repo) => {
    const repoName = repo.name.toLowerCase();
    return repoName.includes(query);
  });
  return finalRepos ;
};

function RepositoriesList() {
  const { loading, error, data } = useQuery(GET_REPOSITORIES);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <ul>
      {data.viewer.repositories.edges.map(({ node }) => (
        <li key={node.name}>{node.name}</li>
      ))}
    </ul>
  );
};

function GetRepos() {
  const { loading, error, data } = useQuery(GET_REPOSITORIES);
  if (loading) return;
  if (error) return;
  var repos = data.viewer.repositories.edges.map(({ node }) => (
    { name: node.name }
  ));
  return (
    repos
  );
};



function App() {
  const { search } = window.location;
  const query = new URLSearchParams(search).get('s');
  const repos1 = GetRepos();
  const [searchQuery, setSearchQuery] = useState(query || '');
  const filteredRepos = filterRepos(repos1, searchQuery);
  // const viewRepos = filteredRepos && filteredRepos.map(({ node }) => (
  //   node.name
  // ));
  console.log(filteredRepos);
  return (
    <div>
      <h2>Repositories ðŸš€</h2>
      < SearchBar
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
      />
      {/* <ul>
        {filteredRepos.map(({ repo }) => (
          <li key={repo.name}>{repo.name}</li>
        ))}
      </ul> */}
      < RepositoriesList />
    </div>
  );
};


ReactDOM.render(
  <ApolloProvider client={client}>
    <App />
  </ApolloProvider>,
  document.getElementById('root'),
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();


