import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';
import './App.css';
import SearchBar from './search';
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";


const client = new ApolloClient({
  uri: "https://api.github.com/graphql",
  cache: new InMemoryCache(),
  headers: {
    authorization: `Bearer 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`
  }
});

const GET_REPOSITORIES = gql`
   query repositories {
    viewer {
        login
        repositories(first: 30) {
            edges {
                node {
                name
                }
            }
        }
    }
}
`;

const { search } = window.location;
const query1 = new URLSearchParams(search).get('s');

const filterRepos = (repos, query1) => {
  if (!query1) {
    return repos;
  }

  return repos.filter((repo) => {
    const repoName = repo.name.toLowerCase();
    return repoName.includes(query1);
  });
};

function RepositoriesList() {
  const { loading, error, data } = useQuery(GET_REPOSITORIES);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  var repos = data.viewer.repositories.edges.map(({ node }) => (
    {name: node.name}
  ));
  return (
    <ul>
      {data.viewer.repositories.edges.map(({ node }) => (
          <li key={node.name}>{node.name}</li>
      ))}
    </ul>
  );
};

function App() {
  return (
    <div>
      <h2>Repositories ðŸš€</h2>
      < SearchBar />
      < RepositoriesList />
    </div>
  );
};


ReactDOM.render(
  <ApolloProvider client={client}>
    <App />
  </ApolloProvider>,
  document.getElementById('root'),
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();


